using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Arkensoft.Exercise.Tests
{
    public class CompareTwoStringDifferenceTests
    {
        [Fact]
        public void FindTheDifference_if_differnce_character_at_the_end()
        {
            var first = "abcd";
            var second = "abcde";
            var difference = new CompareTwoStringDifference();
            var result = difference.FindTheDifference(first, second);

            Assert.Equal('e', result);
        }

        [Fact]
        public void FindTheDifference_if_differnce_character_at_the_begining()
        {
            var first = "abcd";
            var second = "eabcd";
            var difference = new CompareTwoStringDifference();
            var result = difference.FindTheDifference(first, second);

            Assert.Equal('e', result);
        }

        [Fact]
        public void FindTheDifference_if_differnce_character_at_the_middle()
        {
            var first = "abcd";
            var second = "abkcd";
            var difference = new CompareTwoStringDifference();
            var result = difference.FindTheDifference(first, second);

            Assert.Equal('k', result);
        }
        
        [Fact]
        public void FindTheDifference_test_1()
        {
            var first  = "";
            var second = "";
            var difference = new CompareTwoStringDifference();
            var result = difference.FindTheDifference(first, second);

            Assert.Equal('k', result);
        }
    }
}
